import React, { useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import { useNavigate } from 'react-router-dom';

const firebaseConfig = {
  apiKey: 'AIzaSyB7tlY9bDGWxRPVTcPlZ8WbjMMyunIb5vE',
  authDomain: 'drivcon-f00f7.firebaseapp.com',
  projectId: 'drivcon-f00f7',
  // ... other Firebase configurations
};

if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

const Login = () => {
  const [loginEmail, setLoginEmail] = useState('');
  const [loginPassword, setLoginPassword] = useState('');
  const [signupEmail, setSignupEmail] = useState('');
  const [signupPassword, setSignupPassword] = useState('');
  const navigate = useNavigate();

  const handleLoginEmailChange = (e) => {
    setLoginEmail(e.target.value);
  };

  const handleLoginPasswordChange = (e) => {
    setLoginPassword(e.target.value);
  };

  const handleSignupEmailChange = (e) => {
    setSignupEmail(e.target.value);
  };

  const handleSignupPasswordChange = (e) => {
    setSignupPassword(e.target.value);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    firebase
      .auth()
      .signInWithEmailAndPassword(loginEmail, loginPassword)
      .then((userCredential) => {
        // Handle successful login
        const user = userCredential.user;
        console.log('Logged in:', user);

        // Navigate to the portal
        navigate('/');
      })
      .catch((error) => {
        // Handle login error
        console.error(error);
      });
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    firebase
      .auth()
      .createUserWithEmailAndPassword(signupEmail, signupPassword)
      .then((userCredential) => {
        // Handle successful sign-up
        const user = userCredential.user;
        console.log('Signed up:', user);

        // Navigate to the portal
        navigate('/login');
      })
      .catch((error) => {
        // Handle sign-up error
        console.error(error);
      });
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Email:</label>
          <input type="email" value={loginEmail} onChange={handleLoginEmailChange} />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={loginPassword} onChange={handleLoginPasswordChange} />
        </div>
        <button type="submit">Login</button>
      </form>

      <h2>Sign Up</h2>
      <form onSubmit={handleSignUp}>
        <div>
          <label>Email:</label>
          <input type="email" value={signupEmail} onChange={handleSignupEmailChange} />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={signupPassword} onChange={handleSignupPasswordChange} />
        </div>
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
};

export default Login;
