import React, { useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import { Link, useNavigate } from 'react-router-dom';
import whiteLogo from '../drivcon_white.png';

const firebaseConfig = {
    apiKey: 'AIzaSyB7tlY9bDGWxRPVTcPlZ8WbjMMyunIb5vE',
    authDomain: 'drivcon-f00f7.firebaseapp.com',
    projectId: 'drivcon-f00f7',
    // ... other Firebase configurations
};

if (!firebase.apps.length) {
    
}

const Login = () => {
    const [loginEmail, setLoginEmail] = useState('');
    const [loginPassword, setLoginPassword] = useState('');
    const navigate = useNavigate();

    const handleLoginEmailChange = (e) => {
        setLoginEmail(e.target.value);
    };

    const handleLoginPasswordChange = (e) => {
        setLoginPassword(e.target.value);
    };

    const handleLogin = (e) => {
        e.preventDefault();
        firebase
        .auth()
        .signInWithEmailAndPassword(loginEmail, loginPassword)
        .then((userCredential) => {
            // Handle successful login
            const user = userCredential.user;
            console.log('Logged in:', user);

            // Navigate to the portal
            navigate('/');
        })
        .catch((error) => {
            // Handle login error
            console.error(error);
        });
    };

    return (
        <div className='Login'>
            <div className='loginForm'>
                <h2>Login</h2>
                <form onSubmit={handleLogin}>
                    <div>
                        <label>Email:</label>
                        <input type="email" value={loginEmail} onChange={handleLoginEmailChange} />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input type="password" value={loginPassword} onChange={handleLoginPasswordChange} />
                    </div>
                    <button type="submit">Login</button>
                </form>
                <p>Not yet registered? <Link to={'/register'}>Create an account</Link></p>
            </div>
            <div className='authBG'>
                <img src={whiteLogo} alt='white-logo'></img>
            </div>
        </div>
    )
};

export default Login;
