import { FormControl, Button, Modal } from 'react-bootstrap';
import { useState, useEffect } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/database';
import logo from './drivcon_logo.png';
import './App.css';

const firebaseConfig = {
  apiKey: "AIzaSyDNd3kLGt5hielu7VgV3_XlApz1GSH04yg",
  authDomain: "drivcon-b6bb5.firebaseapp.com",
  databaseURL: "https://drivcon-b6bb5-default-rtdb.firebaseio.com",
  projectId: "drivcon-b6bb5",
  storageBucket: "drivcon-b6bb5.appspot.com",
  messagingSenderId: "978848129169",
  appId: "1:978848129169:web:a7ec3816c7f2fe33a85f30"
};

firebase.initializeApp(firebaseConfig);

function App() {
  const [infusions, setInfusions] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState("");

  useEffect(() => {
    // Attach a listener for changes to the "infusions" node
    const infusionsRef = firebase.database().ref('infusions');
    infusionsRef.on('value', (snapshot) => {
      const data = snapshot.val();
      const infusions = Object.entries(data || {}).map(([key, value]) => ({
        id: key,
        fluid_type: value.fluid_type || '',
        total_volume: value.total_volume + " mL" || '',
        infusion_rate: value.infusion_rate + " mL/hr" || '',
        total_time: value.total_time + " hrs." || '',
      }));
      setInfusions(infusions);
    });

    // Attach a listener for changes to the "messages" node
    const messagesRef = firebase.database().ref('events');
    messagesRef.on('value', (snapshot) => {
      const messageObj = snapshot.val();
      if (messageObj) {
        const message = Object.values(messageObj)[0].message;
        setModalMessage(message.replace(/\n/g, " "));
        setShowModal(true);
      }
    });

    // Detach the listener when the component unmounts
    return () => {
      infusionsRef.off();
      messagesRef.off();
    }
  }, []);

  const handleRowClick = (infusion) => {
    setSelectedRow(infusion);
  };

    const handleCloseModal = () => {
    setShowModal(false);
    setModalMessage("");
    }

    const getAlertImage = (message) => {
      if (message.toLowerCase().includes("air-in-line")) {
        return '/icons/icon-bubbles.png';
      }
      if (message.toLowerCase().includes("blood")) {
        return '/icons/icon-blood.png';
      }
      return null;
    };

  return (
    <div className='App'>
      <header className='App-header'>
        <img src={logo} className="App-logo" alt="logo" />
        <p>Infusion Monitoring Portal</p>
        <div className='App-control'>
          <FormControl className='App-search' type='text' placeholder='Search' />
          <Button className='Remove-button'>REMOVE</Button>
        </div>
        <div>
          <table className='table-hover'>
            <thead>
              <tr className='table-header'>
                <th className='theader'>IV Type</th>
                <th className='theader'>VTBI</th>
                <th className='theader'>Rate</th>
                <th className='theader'>Time</th>
              </tr>
            </thead>
            <tbody>
              {infusions.map((infusion) => (
                <tr key={infusion.id} className={`table-body ${selectedRow === infusion ? 'selected-row' : ''}`} onClick={() => handleRowClick(infusion)}>
                  <td>{infusion.fluid_type}</td>
                  <td>{infusion.total_volume}</td>
                  <td>{infusion.infusion_rate}</td>
                  <td>{infusion?.total_time}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <p>Alerts</p>
        <div className='alert-container'>
          <div className='alert-card'>
            <img src='/icons/icon-bubbles.png' alt='bubbles'></img>
          </div>
          <div className='alert-card'>
            <img src='/icons/icon-blood.png' alt='blood'></img>
          </div>
        </div>
      </header>
      <div className='section'>
      {selectedRow && (
          <>
            <div className='other-text'>
              <p>Infusing</p>
              <p className='Fluid-type'>{selectedRow.fluid_type}</p>
            </div>
            <div className='Flow-rate'>
              <div className='flowrate-text'>
                <p className='flowrate-data'>{selectedRow.infusion_rate.split(' ')[0]}</p>
                <p>{selectedRow.infusion_rate.split(' ')[1]}</p>
              </div>
              <p>Flow Rate</p>
            </div>
            <div className='data-cards'>
              <div className='card'>
                <img src='/icons/icon-syringe.png' alt='syringe-icon'></img>
                <p className='other-text'>{selectedRow.total_volume.split(' ')[0]} mL</p>
                <p className='other-text'>VTBI</p>
              </div>
              <div className='card'>
                <img src='/icons/icon-timer.png' alt='syringe-icon'></img>
                <p className='other-text'>{selectedRow.infusion_rate.split(' ')[0]} mL/hr</p>
                <p className='other-text'>Flow Rate</p>
              </div>
              <div className='card'>
                <img src='/icons/icon-soon.png' alt='syringe-icon'></img>
                <p className='other-text'>{selectedRow.total_time.split(' ')[0]} hrs.</p>
                <p className='other-text'>Time Remaining</p>
              </div>
            </div>
          </>
        )}
      </div>
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Body>
          {getAlertImage(modalMessage) && (
              <img src={getAlertImage(modalMessage)} alt='alert' />
            )}
          {JSON.stringify(modalMessage)}</Modal.Body>
        <Modal.Footer>
          <Button onClick={handleCloseModal}>Okay</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default App;
