import React, { useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import { Link, useNavigate } from 'react-router-dom';

const firebaseConfig = {
    apiKey: 'AIzaSyB7tlY9bDGWxRPVTcPlZ8WbjMMyunIb5vE',
    authDomain: 'drivcon-f00f7.firebaseapp.com',
    projectId: 'drivcon-f00f7',
    // ... other Firebase configurations
};

if (!firebase.apps.length) {
    firebase.initializeApp(firebaseConfig);
}

const Register = () => {
    const [signupFName, setSignupFName] = useState('');
    const [signupLName, setSignupLName] = useState('');
    const [signupEmail, setSignupEmail] = useState('');
    const [signupPassword, setSignupPassword] = useState('');
    const navigate = useNavigate();

    const handleSignupFNameChange = (e) => {
        setSignupFName(e.target.value);
    }

    const handleSignupLNameChange = (e) => {
        setSignupLName(e.target.value);
    }

    const handleSignupEmailChange = (e) => {
        setSignupEmail(e.target.value);
    };

    const handleSignupPasswordChange = (e) => {
        setSignupPassword(e.target.value);
    };

    const handleSignUp = (e) => {
        e.preventDefault();
        firebase
        .auth()
        .createUserWithEmailAndPassword(signupEmail, signupPassword)
        .then((userCredential) => {
            // Handle successful sign-up
            const user = userCredential.user;
            console.log('Signed up:', user);

            // Update user's display name with the combined first and last name
            user.updateProfile({
                displayName: signupFName + ' ' + signupLName,
          });

            // Navigate to the portal
            navigate('/login');
        })
        .catch((error) => {
            // Handle sign-up error
            console.error(error);
        });
    };

    return (
        <div>
            <h2>Sign Up</h2>
            <form onSubmit={handleSignUp}>
                <div>
                    <label>First Name:</label>
                    <input type='text' value={signupFName} onChange={handleSignupFNameChange} />
                </div>
                <div>
                    <label>Last Name:</label>
                    <input type='text' value={signupLName} onChange={handleSignupLNameChange} />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" value={signupEmail} onChange={handleSignupEmailChange} />
                </div>
                <div>
                    <label>Password:</label>
                    <input type="password" value={signupPassword} onChange={handleSignupPasswordChange} />
                </div>
                <button type="submit">Sign Up</button>
            </form>
            <p>Have an account already? <Link to={'/login'}>Proceed to login</Link></p>
        </div>
    );
};

export default Register;
