import React, { useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import { Link, useNavigate } from 'react-router-dom';
import whiteLogo from '../drivcon_white.png';
import logo from '../drivcon_logo.png';

const firebaseConfig = {
    apiKey: 'AIzaSyB7tlY9bDGWxRPVTcPlZ8WbjMMyunIb5vE',
    authDomain: 'drivcon-f00f7.firebaseapp.com',
    projectId: 'drivcon-f00f7',
    // ... other Firebase configurations
};

if (!firebase.apps.length) {
    
}

const Login = () => {
    const [loginEmail, setLoginEmail] = useState('');
    const [loginPassword, setLoginPassword] = useState('');
    const navigate = useNavigate();

    const handleLoginEmailChange = (e) => {
        setLoginEmail(e.target.value);
    };

    const handleLoginPasswordChange = (e) => {
        setLoginPassword(e.target.value);
    };

    const handleLogin = (e) => {
        e.preventDefault();
        firebase
        .auth()
        .signInWithEmailAndPassword(loginEmail, loginPassword)
        .then((userCredential) => {
            // Handle successful login
            const user = userCredential.user;
            console.log('Logged in:', user);

            // Navigate to the portal
            navigate('/');
        })
        .catch((error) => {
            // Handle login error
            console.error(error);
        });
    };

    return (
        <div className='Login'>
            <div className='loginForm'>
                <p>WELCOME TO</p>
                <img src={logo} className='logoLogin'></img>
                <p className='loginText'>Login to get updates and monitor every patient's IV fluid status.</p>
                <form onSubmit={handleLogin}>
                    <div>
                        <input
                            className="formLogin"
                            type="email"
                            value={loginEmail}
                            onChange={handleLoginEmailChange}
                            placeholder='Email'
                        />
                    </div>
                    <div>
                        <input
                            className="formLogin"
                            type="password"
                            value={loginPassword}
                            onChange={handleLoginPasswordChange}
                            placeholder='Password'
                        />
                    </div>
                    <button className='authButton' type="submit">LOGIN</button>
                </form>
                <p>Not yet registered? <Link to={'/register'}>Create an account</Link></p>
            </div>
            <div className='authBG'>
                <img src={whiteLogo} alt='white-logo' className='logoLoginn'></img>
            </div>
        </div>
    )
};

export default Login;
